import{_ as i,c as o,o as t,af as r}from"./chunks/framework.B0E2DVLT.js";const c=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"posts/大三上/通信原理/考点知识内容2021.md","filePath":"posts/大三上/通信原理/考点知识内容2021.md"}'),s={name:"posts/大三上/通信原理/考点知识内容2021.md"};function n(u,l,g,a,e,d){return t(),o("div",null,l[0]||(l[0]=[r('<h4 id="一、选择题考点" tabindex="-1">一、选择题考点 <a class="header-anchor" href="#一、选择题考点" aria-label="Permalink to &quot;一、选择题考点&quot;">​</a></h4><ol><li><strong>通信系统分类</strong>： <ul><li>单工、半双工、全双工通信系统的定义和区别。</li></ul></li><li><strong>调制效率</strong>： <ul><li>AM（幅度调制）最大调制效率为50%，解析其限制因素。</li></ul></li><li><strong>误码率与信噪比</strong>： <ul><li>不同调制方式（2ASK、2FSK、2PSK、2DPSK）的误码性能分析。</li></ul></li><li><strong>多普勒效应</strong>： <ul><li>高速移动用户对信号频率的影响。</li></ul></li><li><strong>DSB信号解调方式</strong>： <ul><li>比较包络检波和相干解调适用的场景。</li></ul></li><li><strong>调制带宽与M值关系</strong>： <ul><li>高阶调制方式（MPSK、MASK、MFSK、MQAM）对信号带宽的影响。</li></ul></li><li><strong>模拟信号数字化</strong>： <ul><li>抽样、量化、编码三步流程及对应原理。</li></ul></li><li><strong>波特率与比特率关系</strong>： <ul><li>$R_b = R_s \\cdot \\log_2 M$ ，题目给定16种电平值。</li></ul></li><li><strong>传输带宽与速率</strong>： <ul><li>矩形NRZ脉冲频带特性计算QPSK信号所需带宽。</li></ul></li><li><strong>功率表示单位</strong>： <ul><li>dBm单位计算公式：$P(dBm) = 10 \\cdot \\log_{10}(P(W) \\cdot 1000)$ 。</li></ul></li><li><strong>DPSK解调方式</strong>： <ul><li>非相干解调适用于DPSK，包络检波不适用。</li></ul></li><li><strong>信道划分技术</strong>： <ul><li>CDM、TDM、OFDM、FDM的定义与特点。</li></ul></li><li><strong>符号速率单位</strong>： <ul><li>奈特（nat）、波特（baud）、比特的区别。</li></ul></li><li><strong>码距与纠错能力</strong>： <ul><li>$t = \\left\\lfloor \\frac{d-1}{2} \\right\\rfloor$ ，同时检错能力为d−1</li></ul></li><li><strong>频率复用因子对容量的影响</strong>： <ul><li>频率复用因子增大导致系统容量减小。</li></ul></li></ol><hr><h4 id="二、填空题考点" tabindex="-1">二、填空题考点 <a class="header-anchor" href="#二、填空题考点" aria-label="Permalink to &quot;二、填空题考点&quot;">​</a></h4><ol><li><strong>信息传输速率</strong>： <ul><li>$R = R_s \\cdot \\log_2 M$ 。</li></ul></li><li><strong>蜂窝容量提高措施</strong>： <ul><li>小区面积减小，信道数目增加。</li></ul></li><li><strong>路径损耗</strong>： <ul><li>大尺度损耗是主要影响。</li></ul></li><li><strong>区群大小公式</strong>： <ul><li>$N = i^2 + ij + j^2$ 。</li></ul></li><li><strong>MIMO技术增益</strong>： <ul><li>分集增益和复用增益。</li></ul></li><li><strong>调制误码性能比较</strong>： <ul><li>16QAM&gt;16PSK&gt;16ASK</li></ul></li><li><strong>基带系统带宽</strong>： <ul><li>$B = \\frac{1}{T}$ ，频带利用率为R/B。</li></ul></li><li><strong>码组错误检测与纠正</strong>： <ul><li>纠错能力为 $t = \\left\\lfloor \\frac{d-1}{2} \\right\\rfloor$ ，检测能力为d-1。</li></ul></li><li><strong>无ISI传输条件</strong>： <ul><li>满足奈奎斯特准则：带宽 $\\geq \\frac{1}{2}R_s$</li></ul></li></ol><h4 id="三、问答题考点" tabindex="-1">三、问答题考点 <a class="header-anchor" href="#三、问答题考点" aria-label="Permalink to &quot;三、问答题考点&quot;">​</a></h4><ol><li><strong>频率选择性信道</strong>： <ul><li>定义：不同频率成分衰减不同，造成符号失真。</li><li>危害：导致码间串扰（ISI）。</li></ul></li><li><strong>多天线技术优点</strong>： <ul><li>增加系统容量、提供分集增益和抗干扰能力。</li></ul></li><li><strong>码间串扰（ISI）</strong>： <ul><li>原因：信号带宽受限、信道失真或多径效应。</li></ul></li><li><strong>DSB调制分析</strong>： <ul><li>优势：效率更高，节省频谱。</li><li>解调方式：相干解调。</li><li>LPF作用：移除高频分量，恢复基带信号。</li></ul></li><li><strong>5G移动通信</strong>： <ul><li>特点：大规模天线技术、超密集网络、高频谱利用率。</li></ul></li></ol><hr><h4 id="四、计算题考点" tabindex="-1">四、计算题考点 <a class="header-anchor" href="#四、计算题考点" aria-label="Permalink to &quot;四、计算题考点&quot;">​</a></h4><ol><li><strong>线性分组码校验矩阵</strong>： <ul><li>生成矩阵和校验矩阵的求解方法。</li></ul></li><li><strong>多普勒频移</strong>： <ul><li>$f_d = \\frac{v}{c}f$ ，其中v是速度，f是频率。</li></ul></li><li><strong>区群尺寸与频率复用</strong>： <ul><li>$N = i^2 + ij + j^2$ 计算可能尺寸，复用提高频率利用率。</li></ul></li><li><strong>无ISI传输码率</strong>： <ul><li>最大传输码率与滤波器参数 α 的关系。</li></ul></li><li><strong>调制信号时域特性</strong>： <ul><li>码元中载波周期数计算，OOK和2PSK波形绘制，带宽计算。</li></ul></li></ol>',10)]))}const f=i(s,[["render",n]]);export{c as __pageData,f as default};
